mapper.select:
SELECT id, title, markdown, userId, blogTypeId, tagId, updateDate
FROM blog
WHERE userId = #{userId}

SELECT e.id,e.title,e.markdown,e.userId,e.blogTypeId,e.tagId,e.updateDate,e.tagName,e.typeName FROM (SELECT c.id,c.title,c.markdown,c.userId,c.blogTypeId,c.tagId,c.updateDate,d.tagName,d.typeName FROM blog as c inner join (SELECT a.id as tagId, a.tagName, a.blogTypeId, b.typeName FROM tag as a inner join blogType as b on a.blogTypeId = b.id) as d on c.tagId = d.tagId WHERE c.userId = 6 ORDER BY updateDate DESC) as e
typeName = #{blogType}
AND title LIKE CONCAT('%',#{searchValue}, '%')
